/*²úÉúÖ¸¶¨·¶Î§µÄ²»ÖØ¸´Ëæ»úÕûÊı²¢¶ÔÊı×é¸³Öµ*/
void Random_NR(int A[], int n, int min, int max)
{
	memset(A, 0, sizeof(int)*n);
	if (max < min || max - min + 1 < n)
		return;/*Èç¹û·¶Î§²»·ûºÏÒªÇóÔò¸³ÖµÊ§°Ü*/

	int *B = new int[max - min + 1];
	for (int i = 0; i < max - min + 1; i++)
		B[i] = i + min;
	srand(time(NULL));
	for (int i = 0; i < n; i++)
	{
		int index = rand() % (max - min - i);/*Ëæ»úÉú³ÉÒ»¸öÏÂ±ê*/
		A[i] = B[index];
		/*°ÑÄ©Î²µÄÔªËØ·Åµ½indexµÄÎ»ÖÃ£¬
		Ïàµ±ÓÚ°ÑÓÃ¹ıµÄÔªËØÈÓµ½Ä©Î²£¬
		Ö»ÊÇ£¬ºóÃæÎÒÃÇ½«²»»á·ÃÎÊµ½ºóÃæµÄÔªËØ£¬
		ËùÒÔ²¢²»ĞèÒª°ÑindexÎ»ÖÃµÄÔªËØ¸³Öµ¸øºóÃæµÄÏÂ±êÎ»ÖÃ*/
		B[index] = B[max - min - i];
	}
	delete[]B;
	return;
}
