string RandomIDnum(int minYear, int maxYear)
{

	int year = rand() % (maxYear - minYear + 1) + minYear;
	int month = rand() % (12 - 1 + 1) + 1;
	int day;
	if (month == 1 || month == 3 || month == 5 || month == 7 || month == 8 || month == 10 || month == 12)
		day = rand() % (31 - 1 + 1) + 1;
	else if (month == 4 || month == 6 || month == 9 || month == 11)
		day = rand() % (30 - 1 + 1) + 1;
	else if (month == 2)
		day = rand() % (28 - 1 + 1) + 1;
	int n1 = rand() % (9 - 0 + 0) + 0;
	int n2 = rand() % (9 - 0 + 0) + 0;
	int n3 = rand() % (9 - 0 + 0) + 0;
	int n4 = rand() % (9 - 0 + 0) + 0;
	string IDnum = to_string_my(IDnumSix[rand() % (3518 - 0 + 0) + 0]) + to_string_my(year);
	if (month < 10)
		IDnum += "0";
	IDnum += to_string_my(month);
	if (day < 10)
		IDnum += "0";
	IDnum += to_string_my(day);
	IDnum += to_string_my(n1) + to_string_my(n2) + to_string_my(n3);
	if ((n1 + n2 * n3 + n4) % 5 == 0)
		IDnum += "X";
	else
		IDnum += to_string_my(n4);
	return IDnum;
}

